        log.debug("costTime: {} ms", endTime - startTime);

        log.debug("=====================================");

        long startTime2 = System.currentTimeMillis();
        List&lt;String&gt; l2 = getPriceByCompletableFuture(list, "mysql");
        for (String e : l2) {
            log.debug("{}", e);
